import React, { useEffect, useState } from "react";
import { io } from "socket.io-client";
import axios from "axios";
import ScoreBoard from "./components/ScoreBoard.js";
import ChatRoom from "./components/ChatRoom.js";

const socket = io("http://localhost:3000");

function App() {
  const [scores, setScores] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:3000/api/scores")
      .then((res) => setScores(res.data))
      .catch((err) => console.error("Ï†êÏàò Î°úÎìú Ïã§Ìå®:", err));

    socket.on("scoreUpdate", (newScores) => {
      console.log("üì¢ Ïã§ÏãúÍ∞Ñ Ï†êÏàò ÏóÖÎç∞Ïù¥Ìä∏:", newScores);
      setScores(newScores);
    });

    return () => socket.off("scoreUpdate");
  }, []);

  return (
    <div>
      <h1>‚öæ Ïã§ÏãúÍ∞Ñ KBO Ï†êÏàò</h1>
      <ScoreBoard scores={scores} />
      {scores.map((game) => (
        <ChatRoom key={game.time} gameId={game.time} />
      ))}
    </div>
  );
}

export default App;
