import React, { useState, useEffect } from "react";
import io from "socket.io-client";
import axios from "axios";
import ScoreBoard from "./components/ScoreBoard.js";
import ChatRoom from "./components/ChatRoom.js";
import "./App.css";

const socket = io("http://localhost:5000"); // Î∞±ÏóîÎìú Ï£ºÏÜå

function App() {
  const [games, setGames] = useState([]);
  const [loading, setLoading] = useState(true);

  // üì° ÏÑúÎ≤ÑÏóêÏÑú Ïã§ÏãúÍ∞Ñ Í≤ΩÍ∏∞ Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
  useEffect(() => {
    const fetchGames = async () => {
      try {
        const response = await axios.get("http://localhost:5000/games");
        setGames(response.data);
        setLoading(false);
      } catch (error) {
        console.error("Í≤ΩÍ∏∞ Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Ï§ë Ïò§Î•ò Î∞úÏÉù:", error);
      }
    };

    fetchGames(); // Ï≤´ Î°úÎî© Ïãú Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
    socket.on("updateGames", (newGames) => {
      setGames(newGames);
    });

    return () => {
      socket.off("updateGames");
    };
  }, []);

  return (
    <div className="App">
      <h1>‚öæ Ïã§ÏãúÍ∞Ñ ÏïºÍµ¨ Í≤ΩÍ∏∞ & Ïª§ÎÆ§ÎãàÌã∞</h1>
      <h2>üìÖ ÌòÑÏû¨ Í≤ΩÍ∏∞</h2>

      {loading ? (
        <p>‚è≥ Í≤ΩÍ∏∞ Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Ï§ë...</p>
      ) : (
        <ScoreBoard scores={games} />
      )}

      <ChatRoom />
    </div>
  );
}

export default App;
