  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");

  useEffect(() => {
    socket.emit("joinGame", gameId);
    socket.on(`chatUpdate-${gameId}`, (newMessages) => {
      setMessages(newMessages);
    });

    return () => socket.off(`chatUpdate-${gameId}`);
  }, [gameId]);

