import React, { useState, useEffect } from "react";
import axios from "axios";
import Header from "./components/Header.js";
import GameList from "./components/GameList.js";
import ScoreBoard from "./components/ScoreBoard.js";
import ChatRoom from "./components/ChatRoom.js";
import "./App.css";

function App() {
  const [games, setGames] = useState([]); // Í≤ΩÍ∏∞ Î¶¨Ïä§Ìä∏
  const [selectedGame, setSelectedGame] = useState(null); // ÏÑ†ÌÉùÌïú Í≤ΩÍ∏∞

  // üèü 10Ï¥àÎßàÎã§ Í≤ΩÍ∏∞ Îç∞Ïù¥ÌÑ∞ ÏóÖÎç∞Ïù¥Ìä∏
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/games"); // Î∞±ÏóîÎìú API Ìò∏Ï∂ú
        setGames(response.data);
      } catch (error) {
        console.error("‚ùå Í≤ΩÍ∏∞ Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§ÎäîÎç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.", error);
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 10000); // 10Ï¥àÎßàÎã§ Îç∞Ïù¥ÌÑ∞ ÏóÖÎç∞Ïù¥Ìä∏

    return () => clearInterval(interval); // Ïª¥Ìè¨ÎÑåÌä∏ Ïñ∏ÎßàÏö¥Ìä∏ Ïãú Ï†ïÎ¶¨
  }, []);

  return (
    <div className="app">
      <Header />
      <GameList games={games} setSelectedGame={setSelectedGame} />
      {selectedGame && <ScoreBoard game={selectedGame} />}
      {selectedGame && <ChatRoom game={selectedGame} />}
    </div>
  );
}

export default App;
